var funcoes={init:function(){this.sliderHome(),this.responsiveYouTube(),this.removeAtributosImg()},sliderHome:function(){jQuery(".owl-carousel").owlCarousel({items:1,loop:!0,margin:10,autoplay:!0})},responsiveYouTube:function(){var e=jQuery("iframe[src*='youtube'], iframe[src*='vimeo']");e.each(function(){jQuery(this).removeAttr("height").removeAttr("width").wrap("<div class='embed-container'></div>")})},removeAtributosImg:function(){jQuery("img").each(function(){jQuery(this).removeAttr("width"),jQuery(this).removeAttr("height")})}};jQuery(function(){funcoes.init()});